grantPredicate.expression:
const level  = Float(GetCharacterLevel(GetOwner()));
const lootMax = GetPlayerLootMaxLevel();
const falloff = EvalCurve($#10516612381411114:6064632237548240902$, Abs(lootMax - level));
const chance = EvalCurve($#10516612381411114:6064630650862698497$, level);
RandDouble() < chance * falloff

quantity.expression:
const level = Float(GetCharacterLevel(GetOwner()));
const min   = EvalCurve($#10516612381411114:6064630650928758791$, level);
const max   = EvalCurve($#10516612381411114:6064631031109648384$, level);
const mult  = EvalFormula($#10516612381411113:6064632139336385412$);
const roll  = RandDoubleRange(min*mult,max*mult);
const trunc = int(roll);
const fract = roll - trunc;
trunc + int(RandDouble() < fract)

